export const getBranchesByRoleAndUserId = async (userId, role, body) => {
  try {
    const normalizedRole = role?.toLowerCase();
    const isElevatedRole = ['admin', 'superadmin', 'subagent', 'agent', 'masteradmin'].includes(normalizedRole);

    let branchesData = [];

    if (isElevatedRole) {
      const allBanks = await Bank.find();

      for (const bank of allBanks) {
        const branches = await Branch.find({ bankId: bank._id }).populate('bankId', 'bankName');

        for (const branch of branches) {
          const loanCriteria = await LoanCriteria.findOne({
            bankId: bank._id,
            branchId: branch._id
          });

          branchesData.push({
            ...branch.toObject(),
            bankName: branch.bankId?.bankName || '',
            criteria: loanCriteria?.loanCriteriaList || []
          });
        }
      }
    } else {
      const branches = await Branch.find({ userId });

      branchesData = await Promise.all(
        branches.map(async (branch) => {
          const loanCriteria = await LoanCriteria.findOne({
            bankId: branch.bankId,
            branchId: branch._id
          });

          return {
            ...branch.toObject(),
            criteria: loanCriteria?.loanCriteriaList || []
          };
        })
      );
    }

    // ✅ Step 1: Sort all branches by bankPin
    branchesData.sort((a, b) => a.bankPin.localeCompare(b.bankPin));

    // ✅ Step 2: Find and organize by applicantPin
    if (body?.applicantPin) {
      const index = branchesData.findIndex(branch => branch.bankPin === body.applicantPin);

      if (index !== -1) {
        const previousFive = branchesData.slice(Math.max(0, index - 5), index);
        const current = branchesData[index];
        const nextFive = branchesData.slice(index + 1, index + 6);

        const selectedIds = new Set([
          ...previousFive.map(b => b._id.toString()),
          current._id.toString(),
          ...nextFive.map(b => b._id.toString())
        ]);

        const remaining = branchesData.filter(branch => !selectedIds.has(branch._id.toString()));

        const finalOrderedList = [...previousFive, current, ...nextFive, ...remaining];

        return finalOrderedList;
      } else {
        console.warn(`applicantPin '${body.applicantPin}' not found.`);
        return branchesData;
      }
    }

    // Fallback if no applicantPin provided
    return branchesData;
  } catch (error) {
    throw new Error('Error fetching branches: ' + error.message);
  }
};

