//         },
//         {
//             "id": 4,
//             "key": "completed",
//             "status": "Completed Applications",
//             "count": 0
//         }
//     ]
// }

    // Only allow status changes to 'approved', 'rejected', or 'resubmitted'
    if (!['new', 'pending', "inprogress","rejected", "completed"].includes(status.toLowerCase())) {
      throw new Error('Invalid status change');
    }

    application.status = status;
    application.updatedAt = Date.now();

    const updatedApplication = await application.save();
    return updatedApplication;
  } catch (error) {
    throw new Error('Error updating loan application status: ' + error.message);
  }
}

